import React, { useState } from 'react';
import ReactDOM from 'react-dom/client';
import './App.css';

// API Configuration
const API_BASE_URL = 'http://localhost:8001';

// Types
interface LegalResponse {
  issue_type: string;
  advice: string;
  next_steps: string[];
  emergency_contacts?: Array<{name: string, number: string}>;
  language: string;
}

interface LegalAidCenter {
  id: number;
  name: string;
  address: string;
  phone: string;
  services: string[];
  latitude: number;
  longitude: number;
  district: string;
}

